function hideDiv(b){new Fx.Slide(b).toggle()}function fadeDiv(b){$(b).fade("out")}function showDiv(b){window.addEvent("domready",function(){$(b).setStyle("display","inline");if($("js-flash_success")||$("js-flash_error")){(function(){hideDiv(b)}).delay(5000)}})}if($("js-flash_success")||$("js-flash_error")){showDiv("js-flash_message")}function quoteMessage(i,f){var g=$(f).get("html");var j="[quote="+i+"]"+g+"[/quote]\n";var h=$("js-create_commment_text").get("value");$("js-create_commment_text").set("html",h+j);return false}function destroyContent(b){$(b).set("html","")}function confirmDelete(d,c){if(confirm(LANG_DELETE_FILE_OR_CONTENT)){parent.location.href=c}}if($$(".js-tooltip")){$$(".js-tooltip").each(function(d,e){var f=d.get("title").split("::");d.store("tip:title",f[0]);d.store("tip:text",f[1])});var myTips=new Tips(".js-tooltip");myTips.addEvent("show",function(b){b.fade("in")});myTips.addEvent("hide",function(b){b.fade("out")})}function stripNoAlphaChars(b){b=b.replace(/ /g,"_");b=b.replace(/Ä/g,"Ae");b=b.replace(/ä/g,"ae");b=b.replace(/Ö/g,"Oe");b=b.replace(/ö/g,"oe");b=b.replace(/Ü/g,"Ue");b=b.replace(/ü/g,"ue");b=b.replace(/ß/g,"ss");b=b.replace(/\W/g,"_");return b}function stripSlash(b){b=b.replace(/\//g,"&frasl;");return b}function reloadPage(f,d){var e="js-ajax_reload";$(e).setStyle("display","block").addClass("center");$(e).set("html","<img src='"+d+"/loading.gif' alt='"+LANG_LOADING+"' />");$(e).load(f)}function checkPasswords(){if($("password")&&$("password2")){if($("password").value==$("password2").value){$("icon").set("class","icon-success")}else{$("icon").set("class","icon-close")}}};